let a = 10;
let b = 12;

/* AND operator */
console.log(a < 15 && b > 10); // (true and true) => true
console.log(a > 15 && b > 10); // (false and true) =>  false

/* OR operator */
console.log(a < 15 || b > 10); // (true or true) => true
console.log(a > 15 || b > 10); // (false or true) =>  true

/* ! membalik suatu kondisi */
console.log(!a); // false -> artinya tidak true
